mapper.select:
SELECT a1.[SubmitDate] as sqfqsj, a2.[CustomerName] as name, a2.[CustomerIDNO] as idno, a2.[CustomerMobileNO] as mobile, a2.[CustomerQQ] as qq, b1.[FullDivisionName]+a2.[CustomerResidenceAddressExt] as address, a2.[CustomerUnitName] as dwmc, a2.[CustomerUnitPhoneNO] as dwdh, b2.[FullDivisionName]+a2.[CustomerUnitAddressExt] dwdz, a3.[LoanAmount1] as rzje, a3.[PurchaseTax1] as gzs , a3.[MortgageRate1] as sfbl
FROM [AP].[ApplicationForm] a1
LEFT JOIN [AP].[CustomerInfo] a2 on a1.[ApplicationNO] =a2.[ApplicationNO] LEFT JOIN [AP].[FinanceInfo] a3 on a1.[ApplicationNO] =a3.[ApplicationNO] LEFT JOIN BS.[AdministrativeDivision] b1 on a2.[CustomerResidenceAddress] = b1.[Code] LEFT JOIN BS.[AdministrativeDivision] b2 on a2.[CustomerUnitAddress] = b2.[Code] where CONVERT (VARCHAR (10), a1.[SubmitDate] ,120) =#{sqfqrq}
